INSERT INTO `wp_posts` VALUES (35,1,'2025-03-25 07:43:17','2025-03-25 07:43:17','<!-- wp:heading -->\n<h2 class=\"wp-block-heading\" id=\"block-5872aa01-6c2f-472d-a164-dc324b11672c\">What is Glass Boat Rapa Nui?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Glass Boat Rapa Nui is a fun and unique experience for people of all ages. Located at &lt;place> at Rapa Nui, also known as Easter Island, the service allows for passengers to view the ocean through a window on the floor of the boat, an experience you will not find anywhere else on Rapa Nui.</p>\n<!-- /wp:paragraph -->','Glass Boat Rapa Nui','','inherit','closed','closed','','2-revision-v1','','','2025-03-25 07:43:17','2025-03-25 07:43:17','',2,'http://glassboatrapanui.com/?p=35',0,'revision','',0);
