-->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"large\",\"fontFamily\":\"heading\"} -->\n<p class=\"has-text-align-center has-heading-font-family has-large-font-size\">We give tours over the ocean of Rapa Nui and share in the culture of Rapa Nui</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"26px\",\"style\":{\"layout\":[]}} -->\n<div style=\"height:26px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|custom-black\"}}},\"border\":{\"width\":\"2px\",\"color\":\"#f64e09\",\"radius\":\"20px\"}},\"textColor\":\"custom-black\",\"fontSize\":\"x-large\"} -->\n<h2 class=\"wp-block-heading has-text-align-center has-border-color has-custom-black-color has-text-color has-link-color has-x-large-font-size\" style=\"border-color:#f64e09;border-width:2px;border-radius:20px\">Where is Rapa Nui?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"14px\"} -->\n<div style=\"height:14px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"var:preset|color|custom-black\"}}}},\"textColor\":\"custom-black\",\"fontSize\":\"large\",\"fontFamily\":\"heading\"} -->\n<p class=\"has-text-align-center has-custom-black-color has-text-color has-link-color has-heading-font-family has-large-font-size\">Rapa Nui, also known as Easter Island,<br> is located in Chile</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"26px\",\"style\":{\"layout\":[]}} -->\n<div style=\"height:26px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"style\":{\"border\":{\"width\":\"2px\",\"color\":\"#f64e09\",\"radius\":\"20px\"}}} -->\n<h2 class=\"wp-block-heading has-text-align-center has-border-color\" style=\"border-color:#f64e09;border-width:2px;border-radius:20px\">What is a Glass Boat?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"16px\"} -->\n<div style=\"height:16px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"large\",\"fontFamily\":\"heading\"} -->\n<p class=\"has-text-align-center has-heading-font-family has-large-font-size\">The Glass Boat is a boat with a large glass window on the floor (pictured below)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"26px\",\"style\":{\"layout\":[]}} -->\n<div style=\"height:26px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:group -->\n\n<!-- wp:image {\"id\":85,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"align\":\"wide\",\"className\":\"is-style-rounded\",\"style\":{\"border\":{\"radius\":\"20px\"}}} -->\n<figure class=\"wp-block-image alignwide size-full has-custom-border is-style-rounded\"><img src=\"https://glassboatrapanui.com/wp-content/uploads/2025/03/main-boat.jpg\" alt=\"People standing around the floor window of the boat\" class=\"wp-image-85\" style=\"border-radius:20px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"margin\":{\"top\":\"0\",\"bottom\":\"0\"},\"padding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\"}},\"border\":{\"width\":\"2px\",\"color\":\"#f64e09\",\"radius\":\"20px\"}},\"backgroundColor\":\"base-2\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull has-border-color has-base-2-background-color has-background\" style=\"border-color:#f64e09;border-width:2px;border-radius:20px;margin-top:0;margin-bottom:0;padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"0px\"}},\"layout\":{\"type\":\"flex\",\"orientation\":\"vertical\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"textAlign\":\"center\",\"className\":\"is-style-asterisk\"} -->\n<h2 class=\"wp-block-heading has-text-align-center is-style-asterisk\">Frequently Asked Questions</h2>\n<!-- /wp:heading -->\n\n<!-- wp:spacer {\"height\":\"48px\",\"style\":{\"layout\":{\"flexSize\":\"48px\",\"selfStretch\":\"fixed\"}}} -->\n<div style=\"height:48px\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer --></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|30\",\"left\":\"var:preset|spacing|40\"}}}} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"700\"}},\"fontSize\":\"medium\",\"fontFamily\":\"system-sans-serif\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-system-sans-serif-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:700\">What happens on the glass boat?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"typography\":{\"fontSize\":\"16px\"}}} -->\n<p class=\"has-text-align-left\" style=\"font-size:16px\">There is a tour that provides the experience of navigating and understanding how the sea is connected to the history and current development of Rapa Nui as a Polynesian people.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}},\"fontSize\":\"medium\",\"fontFamily\":\"body\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-body-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:600\">Is it safe?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"typography\":{\"fontSize\":\"16px\"}}} -->\n<p class=\"has-text-align-left\" style=\"font-size:16px\">Yes, the glass boat is safe;<br>- Passengers are required to wear safety jackets<br>- Certified by the army<br>- Certified by the tour office</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}},\"fontSize\":\"medium\",\"fontFamily\":\"body\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-body-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:600\">How and when can I make a reservation?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\",\"style\":{\"typography\":{\"fontSize\":\"16px\"}}} -->\n<p class=\"has-text-align-left\" style=\"font-size:16px\">Get your tickets through our WhatsApp: <br>+5 995 162 0217<br>- The reservation must be made at least 24 hours in advance<br>- The opening hours for reservations are until 9:00 P.M., Monday to Sunday<br>- To confirm the reservation, a payment of $10.000 CLP ≈ $10 is required</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:spacer {\"height\":\"var:preset|spacing|20\"} -->\n<div style=\"height:var(--wp--preset--spacing--20)\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:columns {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|30\",\"left\":\"var:preset|spacing|40\"}}}} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}},\"fontSize\":\"medium\",\"fontFamily\":\"body\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-body-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:600\">What does pricing look like?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>For Summer 2025, the pricing is:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Adults $45.000 CLP ≈ $48 USD<br>Children: $35.000 CLP ≈ $38 USD<br>Locals: $30.000 CLP ≈ $30 USD<br>Snorkeling is another $5.000 CLP ≈ $5 USD</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}},\"fontSize\":\"medium\",\"fontFamily\":\"body\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-body-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:600\">How can I pay?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Accepted Forms of Payment:<br>- Credit card<br>- Foreign currency cash<br>- Chilean pesos (CLP) cash<br>- Bank transfer</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|10\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"textAlign\":\"left\",\"level\":3,\"className\":\"is-style-asterisk\",\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}},\"fontSize\":\"medium\",\"fontFamily\":\"body\"} -->\n<h3 class=\"wp-block-heading has-text-align-left is-style-asterisk has-body-font-family has-medium-font-size\" style=\"font-style:normal;font-weight:600\">What happens if I purchase  ?</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">All snorkel equipment is provided<br>You get to discover the history of Rapa Nui and its sea with the best diver on the island</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','home','','publish','closed','closed','','home','','','2025-04-08 19:14:27','2025-04-08 19:14:27','',0,'http://glassboatrapanui.com/?page_id=45',0,'page','',0);
